// کامپوننت کاروسل تصاویر داخل محتوا
function ImageCarousel({ images }: { images: string[] }) {
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = images.length;

  const handleNext = () => setActiveStep((prev) => (prev + 1) % maxSteps);
  const handleBack = () => setActiveStep((prev) => (prev - 1 + maxSteps) % maxSteps);

  if (images.length === 0) return null;

  return (
    <Box sx={{ position: 'relative', width: '100%', borderRadius: '32px', overflow: 'hidden', my: 8, boxShadow: '0 20px 60px rgba(107, 78, 255, 0.3)', bgcolor: 'rgba(20, 10, 40, 0.45)', backdropFilter: 'blur(20px)', border: '2px solid rgba(107, 78, 255, 0.5)', transition: 'all 0.5s ease', '&:hover': { borderColor: '#A78BFA', boxShadow: '0 28px 80px rgba(107, 78, 255, 0.4)' } }}>
      <Box sx={{ position: 'relative', height: { xs: 400, md: 600 } }}>
        <Image src={images[activeStep]} alt={`تصویر ${activeStep + 1} از مقاله`} fill priority={activeStep === 0} style={{ objectFit: 'cover' }} />
        <Box sx={{ position: 'absolute', inset: 0, background: 'linear-gradient(to top, rgba(10,5,30,0.8), transparent 40%)' }} />
        <Box sx={{ position: 'absolute', bottom: 24, left: '50%', transform: 'translateX(-50%)', bgcolor: 'rgba(107, 78, 255, 0.7)', color: '#fff', px: 4, py: 1.5, borderRadius: '32px', fontWeight: 800, backdropFilter: 'blur(10px)', fontSize: '1.1rem' }}>
          {ConvertToPersianDigit(activeStep + 1)} / {ConvertToPersianDigit(maxSteps)}
        </Box>
      </Box>

      {/* دکمه‌های ناوبری */}
      <Button onClick={handleBack} sx={{ position: 'absolute', left: 16, top: '50%', transform: 'translateY(-50%)', minWidth: 56, height: 56, bgcolor: 'rgba(107, 78, 255, 0.6)', color: '#fff', borderRadius: '50%', '&:hover': { bgcolor: '#A78BFA', transform: 'translateY(-50%) scale(1.1)' } }}>
        <FiChevronLeft size={32} />
      </Button>

      <Button onClick={handleNext} sx={{ position: 'absolute', right: 16, top: '50%', transform: 'translateY(-50%)', minWidth: 56, height: 56, bgcolor: 'rgba(107, 78, 255, 0.6)', color: '#fff', borderRadius: '50%', '&:hover': { bgcolor: '#A78BFA', transform: 'translateY(-50%) scale(1.1)' } }}>
        <FiChevronRight size={32} />
      </Button>
    </Box>
  );
}

